import {
    ORDER_CREATE_REQUEST,
    ORDER_CREATE_SUCCESS,
    ORDER_CREATE_FAIL,
    ORDER_DETAILS_REQUEST,
    ORDER_DETAILS_SUCCESS,
    ORDER_DETAILS_FAIL,
    ORDER_PAY_REQUEST,
    ORDER_PAY_SUCCESS,
    ORDER_PAY_FAIL,
    ORDER_LIST_MY_REQUEST,
    ORDER_LIST_MY_SUCCESS,
    ORDER_LIST_MY_FAIL,
    ORDER_LIST_REQUEST,
    ORDER_LIST_SUCCESS,
    ORDER_LIST_FAIL
    } from '../constants/orderConstams'
import axios from 'axios'

const API = 'http://localhost:5000'

export const createOrder = (order) => async (dispatch, getState )=>{
    try{
        dispatch( { type: ORDER_CREATE_REQUEST })

        const { userLogin:{ userInfo} } = getState()
        const config = {
            headers:{
                'Content-Type': 'application/json',
                Authorization: `Bearer ${userInfo.token}`
            }
        }
        const { data } = await axios.post(`${API}/api/orders`, order, config)
        dispatch( { 
            type: ORDER_CREATE_SUCCESS,
            payload: data
         }) 
    }catch (error){
        dispatch({
            type: ORDER_CREATE_FAIL,
            payload: 
            error.response && error.response.data.message 
            ? error.response.data.message 
            : error.response
        })
    }
}

export const getOrderDetails = (id) => async (dispatch, getState )=>{
    try{
        dispatch( { type: ORDER_DETAILS_REQUEST })

        const { userLogin:{ userInfo} } = getState()
        const config = {
            headers:{
                Authorization: `Bearer ${userInfo.token}`
            }
        }
        const { data } = await axios.get(`${API}/api/orders/${id}`, config)
        dispatch( { 
            type: ORDER_DETAILS_SUCCESS,
            payload: data
         }) 
    }catch (error){
        dispatch({
            type: ORDER_DETAILS_FAIL,
            payload: 
            error.response && error.response.data.message 
            ? error.response.data.message 
            : error.response
        })
    }
}

export const payOrder = (orderId, paymentResult) => async (dispatch, getState )=>{
    try{
        dispatch( { type: ORDER_PAY_REQUEST })

        const { userLogin:{ userInfo} } = getState()
        const config = {
            headers:{
                'Content-Type': 'application/json',
                Authorization: `Bearer ${userInfo.token}`
            }
        }
        const { data } = await axios.put(`${API}/api/orders/${orderId}/pay`, paymentResult, config)
        dispatch( { 
            type: ORDER_PAY_SUCCESS,
            payload: data
         }) 
    }catch (error){
        dispatch({
            type: ORDER_PAY_FAIL,
            payload: 
            error.response && error.response.data.message 
            ? error.response.data.message 
            : error.response
        })
    }
}

export const listMyOrders = () => async (dispatch, getState) => {
    try {
      dispatch({
        type: ORDER_LIST_MY_REQUEST,
      })
  
      const {
        userLogin: { userInfo },
      } = getState()
  
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`,
        },
      }
  
      const { data } = await axios.get(`${API}/api/orders/myorders`, config)
  
      dispatch({
        type: ORDER_LIST_MY_SUCCESS,
        payload: data,
      })
    }catch (error){
        dispatch({
            type: ORDER_LIST_MY_FAIL,
            payload: 
            error.response && error.response.data.message 
            ? error.response.data.message 
            : error.response
        })
    }
  }
